import React, { useEffect, useState, useRef, useCallback } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { motion } from 'framer-motion';
import { Bell, LogOut, Search, User } from 'lucide-react';
import defaultProfile from '../assets/default-profile.png';

const Navbar = ({ currentUser, socket }) => {
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState('');
  const [searchSuggestions, setSearchSuggestions] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isNotificationOpen, setIsNotificationOpen] = useState(false);
  const searchTimeoutRef = useRef(null);

  // Real-time notification handler
  useEffect(() => {
    if (!socket) return;
    socket.on('getNotification', (data) => {
      setNotifications(prev => [...prev, data]);
      setUnreadCount(prev => prev + 1);
    });

    return () => {
      socket.off('getNotification');
    };
  }, [socket]);

  // Debounced search
  useEffect(() => {
    if (!searchQuery.trim()) {
      setSearchSuggestions([]);
      return;
    }

    if (searchTimeoutRef.current) clearTimeout(searchTimeoutRef.current);

    searchTimeoutRef.current = setTimeout(() => {
      fetchSearchSuggestions();
    }, 500);

    return () => clearTimeout(searchTimeoutRef.current);
  }, [searchQuery]);

  const fetchSearchSuggestions = async () => {
    try {
      const res = await axios.get(`/api/profile/search-users?query=${searchQuery}`);
      setSearchSuggestions(res.data.users);
    } catch (err) {
      console.error('Search Error:', err);
    }
  };

  const handleLogout = async () => {
    try {
      await axios.post('/api/auth/logout');
      navigate('/login');
    } catch (err) {
      console.error('Logout Error:', err);
    }
  };

  const handleSearchSelect = (userId) => {
    navigate(`/profile/${userId}`);
    setSearchQuery('');
    setSearchSuggestions([]);
  };

  const toggleNotification = () => {
    setIsNotificationOpen(!isNotificationOpen);
    if (!isNotificationOpen) setUnreadCount(0);
  };

  // Group notifications by type
  const groupedNotifications = useCallback(() => {
    const groups = {};
    notifications.forEach((n) => {
      const key = `${n.senderId}-${n.type}`;
      if (!groups[key]) groups[key] = { ...n, count: 1 };
      else groups[key].count += 1;
    });
    return Object.values(groups);
  }, [notifications]);

  return (
    <nav className="bg-white shadow-md p-4 flex items-center justify-between sticky top-0 z-50">
      {/* Logo */}
      <Link to="/" className="text-xl font-bold">Debatify</Link>

      {/* Desktop Search */}
      <div className="hidden md:flex relative w-1/3">
        <input
          type="text"
          placeholder="Search users..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="border rounded-xl w-full p-2 pl-10 focus:outline-none shadow"
        />
        <Search className="absolute left-3 top-2.5 text-gray-400" size={18} />

        {searchSuggestions.length > 0 && (
          <motion.ul
            initial={{ opacity: 0, y: -10 }}
            animate={{ opacity: 1, y: 0 }}
            className="absolute bg-white shadow-lg rounded-xl w-full mt-1 z-50"
          >
            {searchSuggestions.map((user) => (
              <li
                key={user._id}
                onClick={() => handleSearchSelect(user._id)}
                className="p-2 cursor-pointer hover:bg-gray-100"
              >
                {user.username}
              </li>
            ))}
          </motion.ul>
        )}
      </div>

      {/* Desktop Icons */}
      <div className="hidden md:flex items-center gap-5">
        {/* Notifications */}
        <div className="relative">
          <Bell
            className="cursor-pointer"
            onClick={toggleNotification}
          />
          {unreadCount > 0 && (
            <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center">
              {unreadCount}
            </span>
          )}

          {/* Notification Dropdown */}
          {isNotificationOpen && (
            <motion.div
              initial={{ opacity: 0, y: -10 }}
              animate={{ opacity: 1, y: 0 }}
              className="absolute right-0 mt-2 w-64 bg-white rounded-xl shadow-xl z-50 p-3"
            >
              {groupedNotifications().length > 0 ? (
                groupedNotifications().map((n, index) => (
                  <div key={index} className="p-2 border-b last:border-none">
                    <span className="font-medium">{n.senderName}</span> {n.type} you ({n.count})
                  </div>
                ))
              ) : (
                <p className="text-center text-gray-500">No notifications</p>
              )}
            </motion.div>
          )}
        </div>

        {/* Profile */}
        <Link to={`/profile/${currentUser?._id}`}>
          <img
            src={currentUser?.profilePicture || defaultProfile}
            alt="Profile"
            className="w-10 h-10 rounded-full object-cover"
          />
        </Link>

        {/* Logout */}
        <LogOut
          onClick={handleLogout}
          className="cursor-pointer hover:text-red-500"
        />
      </div>

      {/* Mobile Menu Toggle */}
      <div className="md:hidden">
        <button onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}>
          <User />
        </button>
      </div>

      {/* Mobile Menu */}
      {isMobileMenuOpen && (
        <motion.div
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          className="absolute top-16 right-4 bg-white rounded-xl shadow-xl w-60 p-4 z-50"
        >
          {/* Mobile Search */}
          <div className="relative mb-4">
            <input
              type="text"
              placeholder="Search users..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="border rounded-xl w-full p-2 pl-10 focus:outline-none shadow"
            />
            <Search className="absolute left-3 top-2.5 text-gray-400" size={18} />
            {searchSuggestions.length > 0 && (
              <motion.ul
                initial={{ opacity: 0, y: -10 }}
                animate={{ opacity: 1, y: 0 }}
                className="absolute bg-white shadow-lg rounded-xl w-full mt-1 z-50"
              >
                {searchSuggestions.map((user) => (
                  <li
                    key={user._id}
                    onClick={() => handleSearchSelect(user._id)}
                    className="p-2 cursor-pointer hover:bg-gray-100"
                  >
                    {user.username}
                  </li>
                ))}
              </motion.ul>
            )}
          </div>

          {/* Mobile Notifications */}
          <div className="relative mb-4">
            <div className="flex items-center justify-between">
              <span>Notifications</span>
              <Bell
                onClick={toggleNotification}
                className="cursor-pointer"
              />
              {unreadCount > 0 && (
                <span className="bg-red-500 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center ml-2">
                  {unreadCount}
                </span>
              )}
            </div>
            {isNotificationOpen && (
              <motion.div
                initial={{ opacity: 0, y: -10 }}
                animate={{ opacity: 1, y: 0 }}
                className="bg-white shadow-lg rounded-xl mt-2 p-3"
              >
                {groupedNotifications().length > 0 ? (
                  groupedNotifications().map((n, index) => (
                    <div key={index} className="p-2 border-b last:border-none">
                      <span className="font-medium">{n.senderName}</span> {n.type} you ({n.count})
                    </div>
                  ))
                ) : (
                  <p className="text-center text-gray-500">No notifications</p>
                )}
              </motion.div>
            )}
          </div>

          {/* Profile Link */}
          <Link to={`/profile/${currentUser?._id}`} className="block p-2 rounded hover:bg-gray-100">
            Profile
          </Link>

          {/* Logout */}
          <button onClick={handleLogout} className="flex items-center w-full p-2 mt-2 rounded hover:bg-red-100 text-red-500">
            <LogOut className="mr-2" /> Logout
          </button>
        </motion.div>
      )}
    </nav>
  );
};

export default Navbar;
