import React, { useState, useRef } from "react";
import API from "../api/axiosInstance";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { CheckCircleIcon } from "@heroicons/react/24/outline";

const CreateBlog = () => {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [files, setFiles] = useState([]);
  const [isPrivate, setIsPrivate] = useState(false);
  const [passcode, setPasscode] = useState("");
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);
  const navigate = useNavigate();
  const fileInputRef = useRef(null);

  const handleAddFile = () => {
    fileInputRef.current.click();
  };

  const handleFileChange = (e) => {
    const newFiles = Array.from(e.target.files);
    setFiles((prevFiles) => [...prevFiles, ...newFiles]);
    e.target.value = null;
  };

  const handleRemoveFile = (index) => {
    setFiles((prev) => prev.filter((_, i) => i !== index));
  };

  const resetForm = () => {
    setTitle("");
    setContent("");
    setFiles([]);
    setIsPrivate(false);
    setPasscode("");
    setError(null);
    setLoading(false);
    setSuccess(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setSuccess(false);

    const formData = new FormData();
    formData.append("title", title);
    formData.append("content", content);
    formData.append("isPrivate", isPrivate.toString());
    if (isPrivate) formData.append("passcode", passcode);
    files.forEach((file) => formData.append("files", file));

    try {
      const token = localStorage.getItem("token");
      if (!token) throw new Error("Please log in to create a blog post");

      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 15000);

      await API.post("/blogs", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
          Authorization: `Bearer ${token}`,
        },
        signal: controller.signal,
      });

      clearTimeout(timeoutId);
      setSuccess(true);
      setTimeout(() => {
        resetForm();
        navigate("/blog");
      }, 1500);
    } catch (err) {
      console.error("Error in blog creation:", err);
      if (err.name === "AbortError") {
        setError("Request timed out. Try smaller files or check your connection.");
      } else if (err.response) {
        setError(err.response.data.message || "Failed to create blog post");
      } else {
        setError(err.message || "An unexpected error occurred");
      }
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 via-purple-100 to-pink-100 flex items-center justify-center p-4 md:p-6 md:ml-64">
      {/* (Same JSX you provided, no need to change) */}
      {/* Everything from motion.div to the end of the component remains unchanged */}
      {/* Just fix the CheckCircleIcon import and render it */}
      {/* The rest is correct */}
      {/* Your motion div and form logic is perfect */}
      {/* Your file uploads, conditions, success message, etc., are all good */}
      {/* Just make sure you copy your JSX as-is below */}
    </div>
  );
};

export default CreateBlog;
