import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import API from "../api/axiosInstance";
import { CheckCircleIcon } from "@heroicons/react/outline";

const CreateDebate = () => {
  const [form, setForm] = useState({ title: "", openingArgument: "", category: "" });
  const [isPrivate, setIsPrivate] = useState(false);
  const [passcode, setPasscode] = useState("");
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setSuccess(false);

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        throw new Error("You must be logged in to create a debate.");
      }

      await API.post(
        "/debates",
        {
          ...form,
          isPrivate,
          passcode: isPrivate ? passcode : null,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setSuccess(true);
      setTimeout(() => {
        setForm({ title: "", openingArgument: "", category: "" });
        setIsPrivate(false);
        setPasscode("");
        setLoading(false);
        setSuccess(false);
        navigate("/debate");
      }, 1500);
    } catch (error) {
      console.error("Error creating debate:", error.response?.data);
      setError(error.response?.data?.message || error.message || "Failed to create debate");
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 via-purple-100 to-pink-100 flex items-center justify-center p-4 md:p-6 md:ml-64">
      <motion.div
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8, ease: "easeOut" }}
        className="bg-white rounded-2xl shadow-2xl p-4 md:p-6 w-full max-w-md md:max-w-lg transform transition-all duration-300 hover:shadow-3xl"
      >
        <motion.div
          initial={{ scale: 0 }}
          animate={{ scale: 1 }}
          transition={{ duration: 0.5 }}
          className="text-center"
        >
          <h2 className="text-2xl md:text-3xl font-extrabold text-gray-800 mb-4 md:mb-6">
            Create a <span className="text-yellow-400">Debate</span>
          </h2>
        </motion.div>

        {success ? (
          <motion.div
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.5 }}
            className="text-center"
          >
            <CheckCircleIcon className="h-16 w-16 md:h-20 md:w-20 text-green-500 mx-auto mb-4 animate-pulse" />
            <p className="text-lg md:text-xl font-semibold text-green-600">Debate Created Successfully!</p>
            <p className="text-gray-500 mt-2 text-sm md:text-base">Redirecting to Debate Hub...</p>
          </motion.div>
        ) : (
          <form onSubmit={handleSubmit} className="space-y-4 md:space-y-6">
            <motion.div
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ delay: 0.1, duration: 0.5 }}
            >
              <label htmlFor="title" className="block text-sm md:text-base font-medium text-gray-700 mb-1">
                Title
              </label>
              <input
                type="text"
                id="title"
                name="title"
                placeholder="Enter debate title"
                value={form.title}
                onChange={handleChange}
                className="w-full px-3 py-2 md:px-4 md:py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-yellow-400 focus:border-transparent transition-all duration-300 text-sm md:text-base"
                required
              />
            </motion.div>

            <motion.div
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ delay: 0.2, duration: 0.5 }}
            >
              <label
                htmlFor="openingArgument"
                className="block text-sm md:text-base font-medium text-gray-700 mb-1"
              >
                Opening Argument
              </label>
              <textarea
                id="openingArgument"
                name="openingArgument"
                placeholder="Please start with your Opening Argument..."
                value={form.openingArgument}
                onChange={handleChange}
                className="w-full px-3 py-2 md:px-4 md:py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-yellow-400 focus:border-transparent transition-all duration-300 h-24 md:h-32 resize-none text-sm md:text-base"
                required
              />
            </motion.div>

            <motion.div
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ delay: 0.3, duration: 0.5 }}
            >
              <label htmlFor="category" className="block text-sm md:text-base font-medium text-gray-700 mb-1">
                Category
              </label>
              <select
                id="category"
                name="category"
                value={form.category}
                onChange={handleChange}
                className="w-full px-3 py-2 md:px-4 md:py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-yellow-400 focus:border-transparent transition-all duration-300 appearance-none bg-white text-sm md:text-base"
                required
              >
                <option value="">Select Category</option>
                <option value="Politics_&_Governance">🏛 Politics & Governance</option>
                <option value="Education">📚 Education</option>
                <option value="Technology_&_Science">Hi🌐 Technology & Sciencestory</option>
                <option value="Social_Issues">🌍 Social Issues</option>
                <option value="Business_&_Economy">💼 Business & Economy</option>
                <option value="Environment_&_Sustainability">🌱 Environment & Sustainability</option>
                <option value="Ethics_Morality_&_Philosophy">🧠 Ethics, Morality & Philosophy</option>
                <option value="Entertainment_Media_&_Sports">🕹 Entertainment, Media & Sports</option>
                <option value="Future_&_Innovation">🚀 Future & Innovation</option>
                <option value="Health_&_Lifestyle">🏥 Health & Lifestyle</option>
                <option value="Religion_Caste_&_Social Identity">🕌 Religion, Caste & Social Identity</option>
                <option value="Region_Regionalism">🌍 Region / Regionalism</option>
                <option value="Language_&_Communication">🗣 Language & Communication</option>
                <option value="Entertainment_Fan Wars">🎭 Entertainment Fan Wars</option>
                <option value="Region">Region</option>
                <option value="Region">Region</option>
                <option value="Region">Region</option>
                <option value="Other">Other</option>
              </select>
            </motion.div>

            <motion.div
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ delay: 0.4, duration: 0.5 }}
            >
              <label className="flex items-center space-x-2 text-sm md:text-base font-medium text-gray-700">
                <input
                  type="checkbox"
                  checked={isPrivate}
                  onChange={(e) => setIsPrivate(e.target.checked)}
                  className="h-4 w-4 md:h-5 md:w-5 text-yellow-400 border-gray-300 rounded focus:ring-yellow-400"
                />
                <span>Keep this debate private</span>
              </label>
            </motion.div>

            {isPrivate && (
              <motion.div
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: 0.5, duration: 0.5 }}
              >
                <label
                  htmlFor="passcode"
                  className="block text-sm md:text-base font-medium text-gray-700 mb-1"
                >
                  Set a Passcode
                </label>
                <input
                  type="password"
                  id="passcode"
                  name="passcode"
                  placeholder="Enter a passcode"
                  value={passcode}
                  onChange={(e) => setPasscode(e.target.value)}
                  className="w-full px-3 py-2 md:px-4 md:py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-yellow-400 focus:border-transparent transition-all duration-300 text-sm md:text-base"
                  required={isPrivate}
                />
              </motion.div>
            )}

            {error && (
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ duration: 0.5 }}
                className="p-2 md:p-3 bg-red-100 text-red-700 rounded-xl border border-red-300 text-sm md:text-base"
              >
                {error}
              </motion.div>
            )}

            <motion.button
              type="submit"
              disabled={loading}
              whileHover={{ scale: loading ? 1 : 1.05 }}
              whileTap={{ scale: loading ? 1 : 0.95 }}
              transition={{ duration: 0.3 }}
              className={`w-full bg-gradient-to-r from-blue-500 to-purple-600 text-white py-2 md:py-3 rounded-xl shadow-md hover:from-blue-600 hover:to-purple-700 transition-all duration-300 text-sm md:text-base ${
                loading ? "opacity-70 cursor-not-allowed" : ""
              }`}
            >
              {loading ? (
                <span className="flex items-center justify-center">
                  <svg
                    className="animate-spin h-4 w-4 md:h-5 md:w-5 mr-2 text-white"
                    xmlns="http://www.w3.org/2000/svg"
                    fill="none"
                    viewBox="0 0 24 24"
                  >
                    <circle
                      className="opacity-25"
                      cx="12"
                      cy="12"
                      r="10"
                      stroke="currentColor"
                      strokeWidth="4"
                    ></circle>
                    <path
                      className="opacity-75"
                      fill="currentColor"
                      d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                    ></path>
                  </svg>
                  Creating...
                </span>
              ) : (
                "Create Debate"
              )}
            </motion.button>
          </form>
        )}
      </motion.div>
    </div>
  );
};

export default CreateDebate;
