import React, { useState, useEffect, useCallback } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import API from "../api/axiosInstance";
import { PencilIcon, LockClosedIcon } from "@heroicons/react/solid";

// Blog Card Component
const BlogCard = ({ blog }) => (
  <motion.div
    whileHover={{ scale: 1.03, transition: { duration: 0.2 } }}
    className="relative group bg-white rounded-xl shadow-lg overflow-hidden transition-all duration-300 hover:shadow-xl"
  >
    <Link to={`/blogpage/${blog._id}?section=blog`}>
      <div className="p-4 md:p-6 relative z-10">
        <h3 className="text-lg md:text-xl font-semibold text-gray-800 group-hover:text-white transition-colors duration-300">
          {blog.title || "Untitled Blog"}
        </h3>
        <p className="text-gray-600 group-hover:text-gray-200 mt-2 text-sm md:text-base">
          {blog.content ? blog.content.substring(0, 80) + "..." : "No content available"}
        </p>
        {blog.fileUrl && (
          <p className="mt-1 text-xs md:text-sm text-blue-500 group-hover:text-yellow-300 font-medium transition-colors duration-300">
            {blog.fileUrl.endsWith(".pdf")
              ? "PDF Attached"
              : blog.fileUrl.match(/\.(mp4|webm|ogg)$/)
              ? "Video Attached"
              : blog.fileUrl.match(/\.(mp3|wav|ogg)$/)
              ? "Audio Attached"
              : "Image Attached"}
          </p>
        )}
        <div className="mt-2 flex justify-between items-center">
          <span className="text-xs md:text-sm text-blue-500 group-hover:text-yellow-300 font-medium transition-colors duration-300">
            Votes: {(blog.upvotes || 0) - (blog.downvotes || 0)}
          </span>
          <p className="text-xs md:text-sm text-gray-500 group-hover:text-gray-300 transition-colors duration-300">
            By:{" "}
            <Link to={`/profile/${blog.author?.username || "unknown"}`} className="hover:underline">
              {blog.author?.username || "Unknown"}
            </Link>
            {blog.isPrivate && <LockClosedIcon className="h-3 w-3 inline ml-1 text-red-500" />}
          </p>
        </div>
      </div>
    </Link>
  </motion.div>
);

// See More Card
const SeeMoreCard = () => (
  <motion.div
    whileHover={{ scale: 1.03, transition: { duration: 0.2 } }}
    className="relative group bg-white rounded-xl shadow-lg overflow-hidden transition-all duration-300 hover:shadow-xl"
  >
    <Link to={`/blogs`} className="flex items-center justify-center h-full">
      <div className="p-4 md:p-6 text-center">
        <h3 className="text-lg md:text-xl font-semibold text-blue-600 group-hover:text-white transition-colors duration-300">
          See More Blogs
        </h3>
      </div>
    </Link>
  </motion.div>
);

const Blog = () => {
  const location = useLocation();
  const navigate = useNavigate();

  const [activeSection, setActiveSection] = useState("all"); // Default to "all"
  const [allBlogs, setAllBlogs] = useState([]);
  const [followingBlogs, setFollowingBlogs] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    setActiveSection(params.get("section") || "all");
  }, [location.search]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const [allRes, followingRes] = await Promise.all([
          API.get("/blogs"),
          API.get("/blogs/following"),
        ]);
        setAllBlogs(Array.isArray(allRes.data) ? allRes.data : []);
        setFollowingBlogs(Array.isArray(followingRes.data) ? followingRes.data : []);
      } catch (err) {
        console.error("Error fetching blogs:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const sortByPopularity = useCallback((items) => {
    return [...items].sort((a, b) => {
      const popA = (a.upvotes || 0) + (a.downvotes || 0);
      const popB = (b.upvotes || 0) + (b.downvotes || 0);
      return popB - popA;
    });
  }, []);

  const renderBlogCards = (blogs) => {
    const sorted = sortByPopularity(blogs);
    const cards = sorted.slice(0, 8).map((blog, index) => (
      <motion.div
        key={blog._id}
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: index * 0.1 }}
      >
        <BlogCard blog={blog} />
      </motion.div>
    ));
    cards.push(
      <motion.div
        key="see-more"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: 0.8 }}
      >
        <SeeMoreCard />
      </motion.div>
    );
    return cards;
  };

  const renderSectionToggle = () => (
    <div className="flex flex-col md:flex-row gap-4 mb-6 justify-center items-center w-full">
      {["all", "following"].map((sec) => (
        <motion.button
          key={sec}
          onClick={() => {
            setActiveSection(sec);
            navigate(`/?section=${sec}`);
          }}
          whileHover={{ scale: 1.1, background: "linear-gradient(to right, #3B82F6, #8B5CF6)" }}
          transition={{ duration: 0.3 }}
          className={`px-4 py-2 rounded-lg transition-all duration-300 text-sm md:text-base font-semibold text-white ${
            activeSection === sec
              ? "bg-gradient-to-r from-blue-600 to-purple-600"
              : "bg-gray-200 text-gray-800 hover:bg-gray-300"
          }`}
        >
          {sec === "all" ? "All" : "Following"}
        </motion.button>
      ))}
      <Link
        to="/createblog"
        className="px-4 py-2 rounded-lg bg-yellow-400 text-blue-900 font-semibold hover:bg-yellow-500 text-sm md:text-base"
      >
        Write a New Blog
      </Link>
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-100 via-blue-100 to-green-100 md:ml-64">
      {/* Hero Section */}
      <motion.div
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8, ease: "easeOut" }}
        className="bg-gradient-to-r from-blue-600 to-green-600 text-white py-12 px-4 md:py-16 md:px-5 text-center shadow-lg relative mb-10"
      >
        <motion.div
          animate={{ scale: [1, 1.05, 1] }}
          transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
          className="inline-block"
        >
          <PencilIcon className="h-12 w-12 md:h-16 md:w-16 mx-auto mb-4 text-yellow-300" />
        </motion.div>
        <h1 className="text-4xl md:text-6xl font-extrabold tracking-tight mb-4">
          <span className="text-yellow-300">Blog</span> Hub
        </h1>
        <p className="text-base md:text-xl max-w-xl mx-auto opacity-90">
          Share your thoughts, inspire others, and explore a world of ideas.
        </p>
      </motion.div>

      {/* Main Content */}
      <div className="p-4 md:p-10 max-w-6xl mx-auto">
        {renderSectionToggle()}

        {loading ? (
          <div className="text-center">
            <motion.div
              animate={{ rotate: 360 }}
              transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
              className="inline-block h-6 w-6 md:h-8 md:w-8 border-4 border-blue-500 border-t-transparent rounded-full"
            />
            <p className="text-gray-600 mt-2 text-sm md:text-base">Loading blogs...</p>
          </div>
        ) : (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5 }}
            className="grid gap-4 md:gap-6 grid-cols-1 sm:grid-cols-2 lg:grid-cols-3"
          >
            {activeSection === "all" && renderBlogCards(allBlogs)}
            {activeSection === "following" && renderBlogCards(followingBlogs)}
          </motion.div>
        )}
      </div>
    </div>
  );
};

export default Blog;
