import React, { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { motion } from "framer-motion";
import API from "../api/axiosInstance"; // Your API setup
import { CheckCircleIcon } from "@heroicons/react/outline";

const CreateDebate = () => {
  const [form, setForm] = useState({ title: "", openingArgument: "", category: "" });
  const [isPrivate, setIsPrivate] = useState(false);
  const [passcode, setPasscode] = useState("");
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState(null);

  const navigate = useNavigate();
  const location = useLocation();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setSuccess(false);

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        throw new Error("You must be logged in to create a debate.");
      }

      await API.post(
        "/debates",
        {
          ...form,
          isPrivate,
          passcode: isPrivate ? passcode : null,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setSuccess(true);
      setTimeout(() => {
        // Reset form and state
        setForm({ title: "", openingArgument: "", category: "" });
        setIsPrivate(false);
        setPasscode("");
        setLoading(false);
        setSuccess(false);

        // Determine redirect destination
        const fromProfile = location.state?.fromProfile;
        if (fromProfile) {
          const username = localStorage.getItem("username"); // Assumes username is in localStorage
          navigate(`/profile/${username}`);
        } else {
          navigate("/debate");
        }
      }, 1500); // Delay for success message
    } catch (error) {
      console.error("Error creating debate:", error.response?.data);
      setError(error.response?.data?.message || error.message || "Failed to create debate");
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 via-purple-100 to-pink-100 flex items-center justify-center p-4">
      <motion.div
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8, ease: "easeOut" }}
        className="bg-white rounded-2xl shadow-2xl p-6 w-full max-w-lg"
      >
        <h2 className="text-3xl font-extrabold text-gray-800 mb-6 text-center">
          Create a <span className="text-yellow-400">Debate</span>
        </h2>

        {success ? (
          <motion.div
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.5 }}
            className="text-center"
          >
            <CheckCircleIcon className="h-20 w-20 text-green-500 mx-auto mb-4 animate-pulse" />
            <p className="text-xl font-semibold text-green-600">Debate Created Successfully!</p>
            <p className="text-gray-500 mt-2">
              {location.state?.fromProfile ? "Redirecting to your profile..." : "Redirecting to Debate Hub..."}
            </p>
          </motion.div>
        ) : (
          <form onSubmit={handleSubmit} className="space-y-6">
            <div>
              <label htmlFor="title" className="block text-base font-medium text-gray-700 mb-1">
                Title
              </label>
              <input
                type="text"
                id="title"
                name="title"
                value={form.title}
                onChange={handleChange}
                className="w-full px-4 py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-yellow-400"
                required
              />
            </div>

            <div>
              <label htmlFor="openingArgument" className="block text-base font-medium text-gray-700 mb-1">
                Opening Argument
              </label>
              <textarea
                id="openingArgument"
                name="openingArgument"
                value={form.openingArgument}
                onChange={handleChange}
                className="w-full px-4 py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-yellow-400 h-32"
                required
              />
            </div>

            <div>
              <label htmlFor="category" className="block text-base font-medium text-gray-700 mb-1">
                Category
              </label>
              <select
                id="category"
                name="category"
                value={form.category}
                onChange={handleChange}
                className="w-full px-4 py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-yellow-400"
                required
              >
                <option value="">Select Category</option>
                <option value="Politics">Politics</option>
                <option value="Technology">Technology</option>
                {/* Add other options as needed */}
              </select>
            </div>

            <label className="flex items-center space-x-2">
              <input
                type="checkbox"
                checked={isPrivate}
                onChange={(e) => setIsPrivate(e.target.checked)}
                className="h-5 w-5 text-yellow-400"
              />
              <span>Keep this debate private</span>
            </label>

            {isPrivate && (
              <div>
                <label htmlFor="passcode" className="block text-base font-medium text-gray-700 mb-1">
                  Set a Passcode
                </label>
                <input
                  type="password"
                  id="passcode"
                  value={passcode}
                  onChange={(e) => setPasscode(e.target.value)}
                  className="w-full px-4 py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-yellow-400"
                  required
                />
              </div>
            )}

            {error && (
              <div className="p-3 bg-red-100 text-red-700 rounded-xl">
                {error}
              </div>
            )}

            <button
              type="submit"
              disabled={loading}
              className={`w-full bg-gradient-to-r from-blue-500 to-purple-600 text-white py-3 rounded-xl ${
                loading ? "opacity-70 cursor-not-allowed" : "hover:from-blue-600 hover:to-purple-700"
              }`}
            >
              {loading ? "Creating..." : "Create Debate"}
            </button>
          </form>
        )}
      </motion.div>
    </div>
  );
};

export default CreateDebate;
