import React, { useEffect, useState, useCallback, useMemo } from "react";
import { useParams, Link } from "react-router-dom";
import API from "../api/axiosInstance";
import { motion } from "framer-motion";
import { ThumbUpIcon, ThumbDownIcon, ChatIcon, HeartIcon } from "@heroicons/react/solid";

const DiscussionPage = () => {
  const { id } = useParams();
  const [discussion, setDiscussion] = useState(null);
  const [originalComments, setOriginalComments] = useState([]);
  const [comment, setComment] = useState("");
  const [loading, setLoading] = useState(true);
  const [voting, setVoting] = useState(false);
  const [commenting, setCommenting] = useState(false);
  const [error, setError] = useState(null);
  const [passcode, setPasscode] = useState("");
  const [passcodeRequired, setPasscodeRequired] = useState(false);
  const [sortBy, setSortBy] = useState("latest");

  const fetchDiscussion = useCallback(async (enteredPasscode = null) => {
    try {
      setLoading(true);
      setError(null);
      const url = enteredPasscode
        ? `/discussions/${id}?passcode=${encodeURIComponent(enteredPasscode)}`
        : `/discussions/${id}`;
      const { data } = await API.get(url);
      setDiscussion(data);
      setOriginalComments(data.comments || []);
      setPasscodeRequired(false);
    } catch (err) {
      console.error("Fetch error:", err.response?.data || err.message);
      if (err.response?.status === 401) {
        setPasscodeRequired(true);
        if (enteredPasscode) setError("Incorrect passcode. Please try again.");
      } else {
        setError(err.response?.data?.message || "Failed to load discussion.");
      }
    } finally {
      setLoading(false);
    }
  }, [id]);

  useEffect(() => {
    fetchDiscussion();
  }, [fetchDiscussion]);

  const sortedComments = useMemo(() => {
    const commentsCopy = [...originalComments];
    if (sortBy === "mostLiked") {
      return commentsCopy.sort((a, b) => (b.likes || 0) - (a.likes || 0));
    }
    return commentsCopy.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
  }, [sortBy, originalComments]);

  const handlePasscodeSubmit = (e) => {
    e.preventDefault();
    if (!passcode.trim()) return setError("Please enter a passcode.");
    fetchDiscussion(passcode);
  };

  const handleVote = async (voteType) => {
    try {
      setVoting(true);
      setError(null);
      const token = localStorage.getItem("token");
      if (!token) return setError("You must be logged in to vote.");

      const endpoint = voteType === "upvote" ? `/discussions/${id}/upvote` : `/discussions/${id}/downvote`;
      const { data } = await API.post(endpoint, {}, { headers: { Authorization: `Bearer ${token}` } });

      setDiscussion((prev) => ({
        ...prev,
        upvotes: data.upvotes || 0,
        downvotes: data.downvotes || 0,
        upvotedBy: data.upvotedBy || prev.upvotedBy,
        downvotedBy: data.downvotedBy || prev.downvotedBy,
      }));
    } catch (err) {
      console.error("Voting error:", err.response?.data || err.message);
      setError(err.response?.data?.message || "Failed to vote.");
    } finally {
      setVoting(false);
    }
  };

  const handleComment = async () => {
    try {
      setCommenting(true);
      setError(null);
      const token = localStorage.getItem("token");
      if (!token) return setError("You must be logged in to comment.");
      if (!comment.trim()) return setError("Comment cannot be empty.");

      const { data } = await API.post(
        `/discussions/${id}/comment`,
        { text: comment },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setOriginalComments(data.comments || []);
      setComment("");
    } catch (err) {
      console.error("Commenting error:", err.response?.data || err.message);
      setError(err.response?.data?.message || "Failed to comment.");
    } finally {
      setCommenting(false);
    }
  };

  const handleCommentLike = async (commentId) => {
    try {
      setError(null);
      const token = localStorage.getItem("token");
      if (!token) return setError("You must be logged in to like a comment.");

      const { data } = await API.post(
        `/discussions/${id}/comment/${commentId}/like`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setOriginalComments((prev) =>
        prev.map((c) => (c._id === commentId ? { ...c, likes: data.likes, likedBy: data.likedBy } : c))
      );
    } catch (err) {
      console.error("Liking comment error:", err.response?.data || err.message);
      setError(err.response?.data?.message || "Failed to like comment.");
    }
  };

  if (loading) return <p className="text-center mt-5 text-gray-600">Loading discussion...</p>;
  if (!discussion && !passcodeRequired) return <p className="text-center mt-5 text-red-500">Discussion not found.</p>;

  if (passcodeRequired) {
    return (
      <div className="min-h-screen flex flex-col justify-center items-center bg-gradient-to-r from-blue-50 to-purple-50 p-5 ml-64">
        <h2 className="text-2xl font-bold mb-4">This is a Private Discussion</h2>
        <form onSubmit={handlePasscodeSubmit} className="bg-white shadow-lg rounded-lg p-6 w-full max-w-md">
          <label className="block mb-2 text-gray-700">Enter Passcode:</label>
          <input
            type="password"
            value={passcode}
            onChange={(e) => setPasscode(e.target.value)}
            className="w-full p-3 border rounded-lg mb-4 focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />
          <button
            type="submit"
            className="w-full bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 rounded-lg transition-colors duration-200"
          >
            Submit
          </button>
        </form>
        {error && <p className="mt-4 text-red-600">{error}</p>}
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-r from-blue-50 to-purple-50 p-5 ml-64">
      {error && <div className="mb-4 p-3 bg-red-100 text-red-700 rounded-md text-center">{error}</div>}

      <div className="max-w-3xl mx-auto bg-white shadow-lg rounded-lg p-5">
        <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.5 }}>
          <h2 className="text-4xl font-bold text-gray-800 mb-2">{discussion.title}</h2>
          <p className="text-gray-600 mb-4">{discussion.description}</p>
          <p className="text-sm text-blue-600 mb-4">
            Category: <span className="font-semibold">{discussion.category}</span> | By:{" "}
            <Link to={`/profile/${discussion.author?.username || 'unknown'}`} className="font-semibold hover:underline">
              {discussion.author?.username || "Unknown"}
            </Link> | Created:{" "}
            <span className="font-semibold">
              {discussion.createdAt ? new Date(discussion.createdAt).toLocaleString() : "Unknown"}
            </span>
          </p>

          <div className="flex space-x-4 mb-6">
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => handleVote("upvote")}
              disabled={voting}
              className={`flex items-center px-4 py-2 rounded-lg shadow-md transition-colors duration-200 ${
                voting ? "bg-green-300 cursor-not-allowed" : "bg-green-500 hover:bg-green-600"
              } text-white`}
            >
              <ThumbUpIcon className="h-5 w-5 mr-2" />
              Upvote ({discussion.upvotes || 0})
            </motion.button>
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => handleVote("downvote")}
              disabled={voting}
              className={`flex items-center px-4 py-2 rounded-lg shadow-md transition-colors duration-200 ${
                voting ? "bg-red-300 cursor-not-allowed" : "bg-red-500 hover:bg-red-600"
              } text-white`}
            >
              <ThumbDownIcon className="h-5 w-5 mr-2" />
              Downvote ({discussion.downvotes || 0})
            </motion.button>
          </div>

          <textarea
            value={comment}
            onChange={(e) => setComment(e.target.value)}
            className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 mb-2"
            placeholder="Write a comment..."
            rows="3"
          />
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={handleComment}
            disabled={commenting}
            className={`w-full flex items-center justify-center px-4 py-2 rounded-lg shadow-md transition-colors duration-200 ${
              commenting ? "bg-blue-300 cursor-not-allowed" : "bg-blue-500 hover:bg-blue-600"
            } text-white`}
          >
            <ChatIcon className="h-5 w-5 mr-2" />
            {commenting ? "Posting..." : "Post Comment"}
          </motion.button>
        </motion.div>
      </div>

      <div className="max-w-3xl mx-auto mt-6">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-2xl font-bold text-gray-800">Comments</h3>
          <select
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
            className="px-3 py-1 border rounded-lg bg-white text-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            <option value="latest">Latest</option>
            <option value="mostLiked">Popular</option>
          </select>
        </div>

        <div className="max-h-[calc(100vh-400px)] overflow-y-auto border rounded-lg p-4 bg-white shadow-md">
          {sortedComments.length > 0 ? (
            sortedComments.map((c) => (
              <motion.div
                key={c._id}
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.3 }}
                className="border-b py-3"
              >
                <p className="text-gray-700">{c.text}</p>
                <div className="flex justify-between items-center mt-1">
                  <p className="text-sm text-gray-500">
                    By:{" "}
                    <Link to={`/profile/${c.user?.username || 'unknown'}`} className="hover:underline">
                      {c.user?.username || "Unknown"}
                    </Link> | {new Date(c.createdAt).toLocaleString()}
                  </p>
                  <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    onClick={() => handleCommentLike(c._id)}
                    className="flex items-center px-2 py-1 rounded-lg bg-pink-500 hover:bg-pink-600 text-white text-sm shadow-md transition-colors duration-200"
                  >
                    <HeartIcon className="h-4 w-4 mr-1" />
                    ({c.likes || 0})
                  </motion.button>
                </div>
              </motion.div>
            ))
          ) : (
            <p className="text-gray-500 text-center">No comments yet. Be the first to comment!</p>
          )}
        </div>
      </div>

      <div className="max-w-3xl mx-auto text-center mt-8">
        <Link to="/" className="text-blue-600 hover:underline font-semibold">
          ‚Üê Back to Home
        </Link>
      </div>
    </div>
  );
};

export default DiscussionPage;
