import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import API from "../api/axiosInstance";
import { motion } from "framer-motion";

const Bookmarks = () => {
  const [bookmarks, setBookmarks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBookmarks = async () => {
      try {
        setLoading(true);
        const token = localStorage.getItem("token");
        if (!token) {
          setError("Please log in to view bookmarks");
          return;
        }
        const { data } = await API.get("/bookmarks", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setBookmarks(data);
      } catch (error) {
        setError("Failed to load bookmarks");
        console.error("Error fetching bookmarks:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchBookmarks();
  }, []);

  if (loading) return <p className="text-center mt-10 text-gray-600">Loading bookmarks...</p>;
  if (error) return <p className="text-center mt-10 text-red-500">{error}</p>;

  return (
    <div className="min-h-screen bg-gray-50 md:ml-64 py-8 px-4">
      <h1 className="text-4xl font-bold text-gray-800 mb-8 text-center">Bookmarks</h1>

      {bookmarks.length === 0 ? (
        <p className="text-gray-600 text-center">You haven't bookmarked any posts yet.</p>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {bookmarks.map((blog) => (
            <motion.div
              key={blog._id}
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              whileHover={{ scale: 1.03 }}
              transition={{ duration: 0.3 }}
              className="bg-white rounded-2xl shadow-lg p-5 hover:shadow-2xl"
            >
              <Link to={`/blog/${blog._id}`} className="text-2xl font-semibold text-blue-600 hover:underline">
                {blog.title}
              </Link>
              <p className="text-gray-600 mt-3">By <span className="font-medium">{blog.author?.username || "Unknown"}</span></p>
              <p className="text-gray-500 text-sm mt-2">
                Views: {blog.views || 0} | Bookmarked by {blog.bookmarkedBy?.length || 0} users
              </p>
            </motion.div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Bookmarks;
