import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import API from "../api/axiosInstance";
import { motion } from "framer-motion";

const Bookmarks = () => {
  const [bookmarks, setBookmarks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBookmarks = async () => {
      try {
        setLoading(true);
        const token = localStorage.getItem("token");
        if (!token) {
          setError("Please log in to view bookmarks");
          return;
        }
        const { data } = await API.get("/bookmarks", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setBookmarks(data);
      } catch (error) {
        setError("Failed to load bookmarks");
        console.error("Error fetching bookmarks:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchBookmarks();
  }, []);

  if (loading) return <p className="text-center mt-10 text-gray-600">Loading bookmarks...</p>;
  if (error) return <p className="text-center mt-10 text-red-500">{error}</p>;

  return (
    <div className="min-h-screen bg-gray-50 md:ml-64 py-8">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        <h1 className="text-3xl md:text-4xl font-bold mb-6 md:mb-8 text-gray-800">Your Bookmarks</h1>
        {bookmarks.length === 0 ? (
          <p className="text-gray-600 text-center">You haven't bookmarked any posts yet.</p>
        ) : (
          <div className="space-y-4 md:space-y-6">
            {bookmarks.map((blog) => (
              <motion.div
                key={blog._id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                className="bg-white rounded-xl shadow-lg p-4 md:p-6"
              >
                <Link to={`/blog/${blog._id}`} className="text-xl md:text-2xl font-semibold text-blue-600 hover:underline">
                  {blog.title}
                </Link>
                <p className="text-gray-600 mt-2">By {blog.author?.username || "Unknown"}</p>
                <p className="text-gray-500 text-sm mt-1">
                  Views: {blog.views || 0} | Bookmarked by {blog.bookmarkedBy?.length || 0} users
                </p>
              </motion.div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Bookmarks;
