import React, { useEffect, useState, useRef } from "react";
import { Link, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import API from "../api/axiosInstance";
import { ChatIcon, ArrowLeftIcon, ArrowRightIcon, SearchIcon, LockClosedIcon } from "@heroicons/react/outline";

const Discussion = () => {
  const navigate = useNavigate();
  const [discussions, setDiscussions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [sortBy, setSortBy] = useState("latest");
  const [currentPage, setCurrentPage] = useState(0);
  const [searchQuery, setSearchQuery] = useState("");
  const [filteredDiscussions, setFilteredDiscussions] = useState([]);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);
  const discussionsPerPage = 9;

  // Initial fetch effect
  useEffect(() => {
    const fetchDiscussions = async () => {
      try {
        setLoading(true);
        const { data } = await API.get("/discussions");
        setDiscussions(Array.isArray(data) ? data : []);
      } catch (error) {
        console.error("Error fetching discussions:", error);
        setDiscussions([]);
      } finally {
        setLoading(false);
      }
    };
    fetchDiscussions();
  }, []);

  // Polling effect for new discussions
  useEffect(() => {
    const intervalId = setInterval(async () => {
      try {
        const { data } = await API.get("/discussions");
        const newDiscussions = Array.isArray(data) ? data : [];
        const existingDiscussionIds = new Set(discussions.map((d) => d._id));
        const discussionsToAdd = newDiscussions.filter((d) => !existingDiscussionIds.has(d._id));

        if (discussionsToAdd.length > 0) {
          setDiscussions((prevDiscussions) => [...prevDiscussions, ...discussionsToAdd]);
        }
      } catch (error) {
        console.error("Error polling discussions:", error);
      }
    }, 2000); // Polling interval set to 2s

    return () => clearInterval(intervalId);
  }, [discussions]);

  useEffect(() => {
    if (searchQuery.trim() === "") {
      setFilteredDiscussions([]);
      setIsDropdownOpen(false);
      return;
    }

    const lowerQuery = searchQuery.toLowerCase();
    const results = discussions.filter((discussion) => {
      const title = discussion.title || "";
      const description = discussion.description || "";
      const category = discussion.category || "";
      const username = discussion.author?.username || "";
      return (
        title.toLowerCase().includes(lowerQuery) ||
        description.toLowerCase().includes(lowerQuery) ||
        category.toLowerCase().includes(lowerQuery) ||
        username.toLowerCase().includes(lowerQuery)
      );
    });

    setFilteredDiscussions(results);
    setIsDropdownOpen(results.length > 0);
  }, [searchQuery, discussions]);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  const sortedDiscussions = [...discussions].sort((a, b) => {
    if (sortBy === "latest") return new Date(b.createdAt) - new Date(a.createdAt);
    if (sortBy === "oldest") return new Date(a.createdAt) - new Date(b.createdAt);
    if (sortBy === "popularity") {
      const popularityA = (a.comments?.length || 0) + (a.upvotes?.length || 0) + (a.downvotes?.length || 0);
      const popularityB = (b.comments?.length || 0) + (b.upvotes?.length || 0) + (b.downvotes?.length || 0);
      return popularityB - popularityA;
    }
    return 0;
  });

  const totalPages = Math.ceil(sortedDiscussions.length / discussionsPerPage);
  const paginatedDiscussions = sortedDiscussions.slice(
    currentPage * discussionsPerPage,
    (currentPage + 1) * discussionsPerPage
  );

  const handlePrevPage = () => {
    if (currentPage > 0) setCurrentPage(currentPage - 1);
  };

  const handleNextPage = () => {
    if (currentPage < totalPages - 1) setCurrentPage(currentPage + 1);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (filteredDiscussions.length > 0) {
      navigate(`/discussionpage/${filteredDiscussions[0]._id}`);
      setSearchQuery("");
      setIsDropdownOpen(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 via-purple-100 to-pink-100 md:ml-64">
      {/* Hero Section */}
      <motion.div
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8, ease: "easeOut" }}
        className="bg-gradient-to-r from-blue-600 to-purple-600 text-white py-12 md:py-16 px-4 md:px-5 text-center shadow-lg relative"
      >
        <div className="absolute top-4 right-4 md:right-5" ref={dropdownRef}>
          <form onSubmit={handleSearchSubmit} className="flex items-center">
            <motion.div whileHover={{ scale: 1.02 }} transition={{ duration: 0.3 }}>
              <input
                type="text"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                placeholder="Search discussions..."
                className="px-3 py-2 md:px-4 md:py-2 pr-10 w-52 md:w-64 bg-white/20 backdrop-blur-md text-white placeholder-gray-200 rounded-xl border border-white/30 focus:outline-none focus:ring-2 focus:ring-yellow-300 transition-all duration-300 text-sm md:text-base"
              />
              <button
                type="submit"
                className="absolute right-3 top-1/2 transform -translate-y-1/2 text-yellow-300 hover:text-yellow-200"
              >
                <SearchIcon className="h-4 w-4 md:h-5 md:w-5" />
              </button>
            </motion.div>
          </form>
          {isDropdownOpen && (
            <motion.div
              initial={{ opacity: 0, y: -10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -10 }}
              transition={{ duration: 0.2 }}
              className="absolute top-12 right-0 w-52 md:w-64 bg-white rounded-xl shadow-lg max-h-80 overflow-y-auto z-40"
            >
              {filteredDiscussions.map((discussion) => (
                <Link
                  key={discussion._id}
                  to={`/discussionpage/${discussion._id}`}
                  onClick={() => {
                    setSearchQuery("");
                    setIsDropdownOpen(false);
                  }}
                  className="block px-4 py-2 md:py-3 border-b border-gray-200 hover:bg-gradient-to-r hover:from-blue-500/20 hover:to-purple-500/20 transition-colors duration-200"
                >
                  <h3 className="text-gray-800 font-semibold text-sm md:text-base">
                    {discussion.title || "Untitled Discussion"}
                  </h3>
                  <p className="text-gray-600 text-xs md:text-sm truncate">
                    {discussion.description
                      ? `${discussion.description.substring(0, 30)}...`
                      : "No description available"}
                  </p>
                  <div className="flex justify-between text-xs md:text-sm mt-1">
                    <span className="text-blue-500">{discussion.category || "Uncategorized"}</span>
                    <span className="text-gray-500">
                      By: {discussion.author?.username || "Unknown"}
                      {discussion.isPrivate && (
                        <LockClosedIcon className="h-3 w-3 md:h-4 md:w-4 inline ml-1 text-red-500" />
                      )}
                    </span>
                  </div>
                </Link>
              ))}
            </motion.div>
          )}
        </div>
        <motion.div
          animate={{ scale: [1, 1.05, 1], opacity: [0.8, 1, 0.8] }}
          transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
          className="inline-block"
        >
          <ChatIcon className="h-12 w-12 md:h-16 md:w-16 mx-auto mb-4 text-yellow-300" />
        </motion.div>
        <h1 className="text-4xl md:text-6xl font-extrabold tracking-tight mb-4">
          <span className="text-yellow-300">Discussion</span> Hub
        </h1>
        <p className="text-base md:text-xl max-w-xl md:max-w-2xl mx-auto opacity-90">
          Share your thoughts, connect with others, and dive into meaningful conversations.
        </p>
        <div className="mt-4 md:mt-6 flex justify-center gap-2 md:gap-4">
          <Link
            to="/createDiscussion"
            className="inline-block px-6 py-2 md:px-8 md:py-3 bg-yellow-400 text-blue-900 font-semibold rounded-full shadow-lg hover:bg-yellow-500 transition-all duration-300 transform hover:scale-105 text-sm md:text-base"
          >
            Start a New Discussion
          </Link>
        </div>
      </motion.div>

      {/* Filters and Discussions Section */}
      <div className="p-4 md:p-10 max-w-6xl mx-auto">
        <motion.h2
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.2 }}
          className="text-3xl md:text-4xl font-bold text-gray-800 mb-6 md:mb-8 text-center"
        >
          Active Discussions
        </motion.h2>
        <div className="flex flex-col md:flex-row md:flex-wrap justify-between items-center mb-6 md:mb-8 space-y-4 md:space-y-0">
          <div className="flex space-x-2 md:space-x-4">
            <button
              onClick={() => setSortBy("latest")}
              className={`px-3 py-1 md:px-4 md:py-2 rounded-lg transition-all duration-300 text-sm md:text-base ${
                sortBy === "latest" ? "bg-blue-600 text-white" : "bg-gray-200 text-gray-800 hover:bg-gray-300"
              }`}
            >
              Latest
            </button>
            <button
              onClick={() => setSortBy("oldest")}
              className={`px-3 py-1 md:px-4 md:py-2 rounded-lg transition-all duration-300 text-sm md:text-base ${
                sortBy === "oldest" ? "bg-blue-600 text-white" : "bg-gray-200 text-gray-800 hover:bg-gray-300"
              }`}
            >
              Oldest
            </button>
            <button
              onClick={() => setSortBy("popularity")}
              className={`px-3 py-1 md:px-4 md:py-2 rounded-lg transition-all duration-300 text-sm md:text-base ${
                sortBy === "popularity" ? "bg-blue-600 text-white" : "bg-gray-200 text-gray-800 hover:bg-gray-300"
              }`}
            >
              Popularity
            </button>
          </div>
          <div className="flex space-x-4 items-center">
            <button
              onClick={handlePrevPage}
              disabled={currentPage === 0}
              className="px-2 py-1 md:px-3 md:py-1 rounded-full bg-gray-200 text-gray-800 hover:bg-gray-300 transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              <ArrowLeftIcon className="h-4 w-4 md:h-5 md:w-5" />
            </button>
            <span className="text-gray-700 text-sm md:text-base">Page {currentPage + 1} of {totalPages}</span>
            <button
              onClick={handleNextPage}
              disabled={currentPage === totalPages - 1}
              className="px-2 py-1 md:px-3 md:py-1 rounded-full bg-gray-200 text-gray-800 hover:bg-gray-300 transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              <ArrowRightIcon className="h-4 w-4 md:h-5 md:w-5" />
            </button>
          </div>
        </div>
        {loading ? (
          <div className="text-center">
            <motion.div
              animate={{ rotate: 360 }}
              transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
              className="inline-block h-6 w-6 md:h-8 md:w-8 border-4 border-blue-500 border-t-transparent rounded-full"
            />
            <p className="text-gray-600 mt-2 text-sm md:text-base">Loading discussions...</p>
          </div>
        ) : discussions.length === 0 ? (
          <motion.p
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5 }}
            className="text-center text-gray-500 text-base md:text-lg"
          >
            No discussions yet. Be the first to start one!
          </motion.p>
        ) : (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5, delay: 0.4 }}
            className="grid gap-4 md:gap-6 grid-cols-1 sm:grid-cols-2 lg:grid-cols-3"
          >
            {paginatedDiscussions.map((discussion, index) => (
              <motion.div
                key={discussion._id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
                whileHover={{ scale: 1.03, transition: { duration: 0.2 } }}
                className="relative group bg-white rounded-xl shadow-lg overflow-hidden transition-all duration-300 hover:shadow-xl"
              >
                <Link to={`/discussionpage/${discussion._id}`}>
                  <div className="absolute inset-0 bg-gradient-to-t from-blue-500/80 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
                  <div className="p-4 md:p-6 relative z-10">
                    <h3 className="text-lg md:text-xl font-semibold text-gray-800 group-hover:text-white transition-colors duration-300">
                      {discussion.title || "Untitled Discussion"}
                    </h3>
                    <p className="text-gray-600 group-hover:text-gray-200 mt-2 text-sm md:text-base">
                      {discussion.description
                        ? discussion.description.substring(0, 80) + "..."
                        : "No description"}
                    </p>
                    <p className="text-xs md:text-sm text-blue-500 group-hover:text-yellow-300 mt-3 font-medium transition-colors duration-300">
                      {discussion.category || "Uncategorized"}
                    </p>
                    <p className="text-xs md:text-sm text-gray-500 group-hover:text-gray-300 mt-1 transition-colors duration-300">
                      By: {discussion.author?.username || "Unknown"}
                      {discussion.isPrivate && (
                        <LockClosedIcon className="h-3 w-3 md:h-4 md:w-4 inline ml-1 text-red-500" />
                      )}
                    </p>
                  </div>
                </Link>
              </motion.div>
            ))}
          </motion.div>
        )}
      </div>
    </div>
  );
};

export default Discussion;
