// src/components/MobileFooter.js
import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import {
  HomeIcon,
  ChatAlt2Icon,
  UsersIcon,
  BookOpenIcon,
  UserCircleIcon,
} from "@heroicons/react/outline";
import API from "../api/axiosInstance"; // Ensure this matches your Navbar's import

const MobileFooter = () => {
  const navigate = useNavigate();
  const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem("token"));
  const [username, setUsername] = useState("");

  // Fetch user info and poll for auth changes
  useEffect(() => {
    const checkAuthStatus = async () => {
      const token = localStorage.getItem("token");
      const newAuthStatus = !!token;

      // Log current state for debugging
      console.log("MobileFooter - Token:", token);
      console.log("MobileFooter - isAuthenticated:", newAuthStatus);

      if (token) {
        try {
          const res = await API.get("/users/profile/me", {
            headers: { Authorization: `Bearer ${token}` },
          });
          console.log("MobileFooter - API Response:", res.data);
          setUsername(res.data.username);
          setIsAuthenticated(true); // Ensure this is set after successful API call
        } catch (err) {
          console.error("MobileFooter - Error fetching user profile:", err);
          setUsername("");
          setIsAuthenticated(false);
          localStorage.removeItem("token"); // Clear invalid token
        }
      } else {
        setUsername("");
        setIsAuthenticated(false);
      }
    };

    checkAuthStatus(); // Initial check
    const authPollInterval = setInterval(checkAuthStatus, 1000); // Poll every second
    return () => clearInterval(authPollInterval);
  }, []); // Removed isAuthenticated from dependencies to avoid re-fetching unnecessarily

  // Handle profile click based on authentication
  const handleProfileClick = (e) => {
    e.preventDefault();
    console.log("MobileFooter - Profile Click - isAuthenticated:", isAuthenticated);
    console.log("MobileFooter - Profile Click - username:", username);
    if (isAuthenticated && username) {
      navigate(`/profile/${username}`);
    } else {
      navigate("/login");
    }
  };

  // Animation variants for icons
  const iconVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        type: "spring",
        stiffness: 300,
        damping: 20,
      },
    },
    hover: {
      scale: 1.2,
      color: "#2563eb", // blue-600
      transition: { duration: 0.2 },
    },
  };

  return (
    <motion.div
      className="fixed bottom-0 w-full bg-white border-t border-gray-200 shadow-lg md:hidden z-50"
      initial={{ y: 100 }}
      animate={{ y: 0 }}
      transition={{ type: "spring", stiffness: 200, damping: 30 }}
    >
      <div className="flex justify-between items-center px-6 py-3 bg-gradient-to-r from-gray-50 to-gray-100">
        <motion.div variants={iconVariants} initial="hidden" animate="visible" whileHover="hover">
          <Link to="/" className="flex flex-col items-center text-gray-600">
            <HomeIcon className="h-6 w-6" />
            <span className="text-xs mt-1 font-medium">Home</span>
          </Link>
        </motion.div>

        <motion.div variants={iconVariants} initial="hidden" animate="visible" whileHover="hover">
          <Link to="/discussion" className="flex flex-col items-center text-gray-600">
            <ChatAlt2Icon className="h-6 w-6" />
            <span className="text-xs mt-1 font-medium">Discussions</span>
          </Link>
        </motion.div>

        <motion.div variants={iconVariants} initial="hidden" animate="visible" whileHover="hover">
          <Link to="/debate" className="flex flex-col items-center text-gray-600">
            <UsersIcon className="h-6 w-6" />
            <span className="text-xs mt-1 font-medium">Debates</span>
          </Link>
        </motion.div>

        <motion.div variants={iconVariants} initial="hidden" animate="visible" whileHover="hover">
          <Link to="/blog" className="flex flex-col items-center text-gray-600">
            <BookOpenIcon className="h-6 w-6" />
            <span className="text-xs mt-1 font-medium">Blogs</span>
          </Link>
        </motion.div>

        <motion.div variants={iconVariants} initial="hidden" animate="visible" whileHover="hover">
          <Link
            to={isAuthenticated && username ? `/profile/${username}` : "/login"}
            onClick={handleProfileClick}
            className="flex flex-col items-center text-gray-600"
          >
            <UserCircleIcon className="h-6 w-6" />
            <span className="text-xs mt-1 font-medium">Profile</span>
          </Link>
        </motion.div>
      </div>
    </motion.div>
  );
};

export default MobileFooter;
