// src/components/Inbox.js
import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import API from "../api/axiosInstance";
import { ChatIcon } from "@heroicons/react/outline";

const Inbox = () => {
  const [conversations, setConversations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const currentUserId = localStorage.getItem("userId");

  // Fetch conversations on mount
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token || !currentUserId) {
      navigate("/login");
      return;
    }

    const fetchInbox = async () => {
      try {
        setLoading(true);
        const { data } = await API.get("/inbox", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setConversations(Array.isArray(data) ? data : []);
      } catch (err) {
        console.error("Error fetching inbox:", err.response?.data || err.message);
        setError("Failed to load inbox");
      } finally {
        setLoading(false);
      }
    };

    fetchInbox();
  }, [navigate, currentUserId]);

  if (loading) {
    return (
      <div className="min-h-screen flex justify-center items-center">
        <motion.div
          animate={{ rotate: 360 }}
          transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
          className="h-8 w-8 border-4 border-blue-500 border-t-transparent rounded-full"
        />
      </div>
    );
  }

  if (error) {
    return <div className="p-4 text-red-500 text-center">{error}</div>;
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 via-purple-100 to-pink-100 p-5 md:p-10 max-w-4xl mx-auto mt-16">
      {/* Header */}
      <motion.div
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8, ease: "easeOut" }}
        className="text-center mb-8"
      >
        <ChatIcon className="h-12 w-12 mx-auto text-blue-600 mb-4" />
        <h1 className="text-4xl font-bold text-gray-800">Messages</h1>
        <p className="text-gray-600 mt-2">Your recent conversations</p>
      </motion.div>

      {/* Conversations List */}
      {conversations.length === 0 ? (
        <motion.p
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
          className="text-center text-gray-500 text-lg"
        >
          No conversations yet. Start one now!
        </motion.p>
      ) : (
        <motion.ul
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5, delay: 0.2 }}
          className="space-y-4"
        >
          {conversations.map((chat, index) => {
            const otherUser = chat.sender._id === currentUserId ? chat.receiver : chat.sender;
            return (
              <motion.li
                key={chat._id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
                whileHover={{ scale: 1.02 }}
                className="bg-white rounded-lg shadow-lg p-4 hover:bg-gray-50 transition-all duration-300"
              >
                <Link to={`/chat/${otherUser._id}`} className="flex items-center space-x-4">
                  <div className="flex-shrink-0">
                    <div className="h-12 w-12 rounded-full bg-blue-500 flex items-center justify-center text-white font-semibold">
                      {otherUser.username[0].toUpperCase()}
                    </div>
                  </div>
                  <div className="flex-1">
                    <p className="font-semibold text-gray-800">{otherUser.username}</p>
                    <p className="text-gray-600 truncate">
                      {chat.text || (chat.image ? "Sent an image" : "No message")}
                    </p>
                    <p className="text-xs text-gray-500 mt-1">
                      {new Date(chat.timestamp).toLocaleString()}
                    </p>
                  </div>
                  {!chat.seen && chat.receiver._id === currentUserId && (
                    <span className="bg-blue-500 text-white text-xs px-2 py-1 rounded-full">
                      New
                    </span>
                  )}
                </Link>
              </motion.li>
            );
          })}
        </motion.ul>
      )}
    </div>
  );
};

export default Inbox;
