import React, { useState } from "react";
import { motion } from "framer-motion";
import { MailIcon, QuestionMarkCircleIcon } from "@heroicons/react/outline";

const Support = () => {
  const [form, setForm] = useState({ name: "", email: "", message: "" });
  const [submitted, setSubmitted] = useState(false);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      // Simulate an API call (replace with actual API.post("/support", form) if integrated)
      await new Promise((resolve) => setTimeout(resolve, 1500));
      setSubmitted(true);
    } catch (error) {
      console.error("Error submitting support request:", error);
      alert("Failed to send your message. Please try again.");
      setLoading(false);
    }
  };

  const faqs = [
    {
      question: "How do I create a Discussion?",
      answer:
        "To create a Discussion, log in to your account, click on 'Start a Discussion' from the homepage or navbar, fill out the form with your Discussion details, and submit.",
    },
    {
      question: "Can I vote or comment without logging in?",
      answer:
        "No, you need to be logged in to vote or comment on Discussions. Please log in or register to participate.",
    },
    {
      question: "How do I reset my password?",
      answer:
        "Password reset is not yet implemented. Please contact support at support@debatify.com, and we’ll assist you.",
    },
    {
      question: "Why can’t I see some Discussions?",
      answer:
        "If you can’t see Discussions, ensure you’re logged in. If the issue persists, there may be no Discussions available, or there might be a server issue. Contact support for help.",
    },
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 via-purple-100 to-pink-100">
      {/* Top Section */}
      <motion.div
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8, ease: "easeOut" }}
        className="py-16 px-5 text-center"
      >
        <motion.div
          animate={{ scale: [1, 1.05, 1], opacity: [0.8, 1, 0.8] }}
          transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
          className="inline-block"
        >
          <MailIcon className="h-16 w-16 mx-auto mb-4 text-yellow-300" />
        </motion.div>
        <h1 className="text-5xl md:text-6xl font-extrabold tracking-tight mb-4 text-gray-800">
          Get <span className="text-yellow-300">Support</span>
        </h1>
        <p className="text-lg md:text-xl max-w-2xl mx-auto text-gray-600">
          We’re here to help! Reach out with your questions or concerns, and we’ll
          get back to you as soon as possible.
        </p>
      </motion.div>

      {/* FAQ Section */}
      <div className="p-5 md:p-10 max-w-3xl mx-auto">
        <motion.h2
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.2 }}
          className="text-4xl font-bold text-gray-800 mb-8 text-center"
        >
          Frequently Asked Questions
        </motion.h2>
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5, delay: 0.4 }}
          className="space-y-6"
        >
          {faqs.map((faq, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
              className="bg-white rounded-xl shadow-lg p-6"
            >
              <div className="flex items-start space-x-3">
                <QuestionMarkCircleIcon className="h-6 w-6 text-blue-500 flex-shrink-0" />
                <div>
                  <h3 className="text-lg font-semibold text-gray-800">
                    {faq.question}
                  </h3>
                  <p className="text-gray-600 mt-1">{faq.answer}</p>
                </div>
              </div>
            </motion.div>
          ))}
        </motion.div>
      </div>

      {/* Contact Form Section ("Send Us a Message" Box) */}
      <div className="p-5 md:p-10 max-w-3xl mx-auto">
        {submitted ? (
          <motion.div
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.5 }}
            className="text-center"
          >
            <MailIcon className="h-20 w-20 text-green-500 mx-auto mb-4 animate-pulse" />
            <p className="text-xl font-semibold text-green-600">
              Message Sent Successfully!
            </p>
            <p className="text-gray-500 mt-2">We’ll get back to you soon.</p>
          </motion.div>
        ) : (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            className="bg-white rounded-2xl shadow-2xl p-6 transform transition-all duration-300 hover:shadow-3xl"
          >
            <h2 className="text-2xl font-bold text-gray-800 mb-6 text-center">
              Send Us a Message
            </h2>
            <form onSubmit={handleSubmit} className="space-y-6">
              <motion.div
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: 0.1, duration: 0.5 }}
              >
                <label
                  htmlFor="name"
                  className="block text-sm font-medium text-gray-700 mb-1"
                >
                  Name
                </label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  placeholder="Your name"
                  value={form.name}
                  onChange={handleChange}
                  className="w-full px-4 py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-yellow-400 focus:border-transparent transition-all duration-300"
                  required
                />
              </motion.div>

              <motion.div
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: 0.2, duration: 0.5 }}
              >
                <label
                  htmlFor="email"
                  className="block text-sm font-medium text-gray-700 mb-1"
                >
                  Email
                </label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  placeholder="Your email"
                  value={form.email}
                  onChange={handleChange}
                  className="w-full px-4 py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-yellow-400 focus:border-transparent transition-all duration-300"
                  required
                />
              </motion.div>

              <motion.div
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: 0.3, duration: 0.5 }}
              >
                <label
                  htmlFor="message"
                  className="block text-sm font-medium text-gray-700 mb-1"
                >
                  Message
                </label>
                <textarea
                  id="message"
                  name="message"
                  placeholder="Your message"
                  value={form.message}
                  onChange={handleChange}
                  className="w-full px-4 py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-yellow-400 focus:border-transparent transition-all duration-300 h-32 resize-none"
                  required
                />
              </motion.div>

              <motion.button
                type="submit"
                disabled={loading}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                transition={{ duration: 0.3 }}
                className={`w-full bg-gradient-to-r from-blue-500 to-purple-600 text-white p-2 rounded-xl shadow-md hover:from-blue-600 hover:to-purple-700 transition-all duration-300 ${
                  loading ? "opacity-70 cursor-not-allowed" : ""
                }`}
              >
                {loading ? (
                  <span className="flex items-center justify-center">
                    <svg
                      className="animate-spin h-5 w-5 mr-2 text-white"
                      xmlns="http://www.w3.org/2000/svg"
                      fill="none"
                      viewBox="0 0 24 24"
                    >
                      <circle
                        className="opacity-25"
                        cx="12"
                        cy="12"
                        r="10"
                        stroke="currentColor"
                        strokeWidth="4"
                      ></circle>
                      <path
                        className="opacity-75"
                        fill="currentColor"
                        d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                      ></path>
                    </svg>
                    Sending...
                  </span>
                ) : (
                  "Send Message"
                )}
              </motion.button>
            </form>
          </motion.div>
        )}
      </div>

      {/* Contact Information */}
      <div className="p-5 md:p-10 max-w-3xl mx-auto text-center">
        <motion.h2
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.2 }}
          className="text-3xl font-bold text-gray-800 mb-6"
        >
          Contact Us Directly
        </motion.h2>
        <motion.p
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5, delay: 0.4 }}
          className="text-gray-600"
        >
          Email us at{" "}
          <a
            href="mailto:support@debatify.com"
            className="text-blue-500 hover:text-blue-600 underline transition-colors duration-300"
          >
            support@debatify.com
          </a>{" "}
          or call us at{" "}
          <a
            href="tel:+1234567890"
            className="text-blue-500 hover:text-blue-600 underline transition-colors duration-300"
          >
            +1 (234) 567-890
          </a>
          .
        </motion.p>
      </div>
    </div>
  );
};

export default Support;
