import React, { useState, useEffect, useCallback } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import API from "../api";
import { LockClosedIcon } from "@heroicons/react/solid";

// A compact card for Discussions
const DiscussionCard = ({ discussion }) => (
  <Link
    to={`/discussionpage/${discussion._id}?section=discussions`}
    className="relative group rounded-xl overflow-hidden shadow hover:shadow-lg transition transform hover:scale-105 bg-white"
  >
    <div className="absolute inset-0 bg-gradient-to-t from-blue-500/80 to-transparent opacity-0 group-hover:opacity-100 transition duration-300" />
    <div className="p-3 md:p-4 relative z-10">
      <h3 className="text-base font-semibold text-gray-800 group-hover:text-white">
        {discussion.title || "Untitled Discussion"}
      </h3>
      <p className="text-gray-600 group-hover:text-gray-200 mt-1 text-sm">
        {discussion.description
          ? discussion.description.substring(0, 60) + "..."
          : "No description"}
      </p>
      <p className="text-xs text-blue-500 group-hover:text-yellow-300 mt-1">
        {discussion.category || "Uncategorized"}
      </p>
      <p className="text-xs text-gray-500 group-hover:text-gray-300 mt-1">
        By:{" "}
        <Link to={`/profile/${discussion.author?.username || "unknown"}`} className="hover:underline">
          {discussion.author?.username || "Unknown"}
        </Link>
        {discussion.isPrivate && (
          <LockClosedIcon className="h-3 w-3 inline ml-1 text-red-500" />
        )}
      </p>
    </div>
  </Link>
);

// A compact card for Debates
const DebateCard = ({ debate }) => (
  <Link
    to={`/debatepage/${debate._id}?section=debates`}
    className="block p-3 rounded-xl shadow border border-gray-200 hover:bg-gradient-to-r hover:from-teal-500/20 hover:to-blue-500/20 transition-colors duration-200 bg-white"
  >
    <h3 className="text-gray-800 font-semibold text-sm">
      {debate.title || "Untitled Debate"}
    </h3>
    <p className="text-gray-600 text-xs mt-1 truncate">
      {debate.openingArgument
        ? debate.openingArgument.substring(0, 60) + "..."
        : "No argument"}
    </p>
    <div className="flex justify-between text-xs mt-2">
      <span className="text-teal-500">{debate.category || "Uncategorized"}</span>
      <span className="text-gray-500">
        By:{" "}
        <Link to={`/profile/${debate.author?.username || "unknown"}`} className="hover:underline">
          {debate.author?.username || "Unknown"}
        </Link>
        {debate.isPrivate && (
          <LockClosedIcon className="h-3 w-3 inline ml-1 text-red-500" />
        )}
      </span>
    </div>
  </Link>
);

// A compact card for Blogs
const BlogCard = ({ blog }) => (
  <Link
    to={`/blogpage/${blog._id}?section=blogs`}
    className="relative group rounded-xl overflow-hidden shadow hover:shadow-lg transition transform hover:scale-105 bg-white"
  >
    <div className="absolute inset-0 bg-gradient-to-t from-blue-500/80 to-transparent opacity-0 group-hover:opacity-100 transition duration-300" />
    <div className="p-3 md:p-4 relative z-10">
      <h3 className="text-base font-semibold text-gray-800 group-hover:text-white">
        {blog.title || "Untitled Blog"}
      </h3>
      <p className="text-gray-600 group-hover:text-gray-200 mt-1 text-sm">
        {blog.content ? blog.content.substring(0, 60) + "..." : "No content available"}
      </p>
      {blog.fileUrl && (
        <p className="mt-1 text-xs text-blue-500 group-hover:text-yellow-300">
          {blog.fileUrl.endsWith(".pdf")
            ? "PDF Attached"
            : blog.fileUrl.match(/\.(mp4|webm|ogg)$/)
            ? "Video Attached"
            : blog.fileUrl.match(/\.(mp3|wav|ogg)$/)
            ? "Audio Attached"
            : "Image Attached"}
        </p>
      )}
      <div className="mt-2 flex justify-between items-center">
        <span className="text-xs text-blue-500 group-hover:text-yellow-300 font-medium">
          Votes: {(blog.upvotes || 0) - (blog.downvotes || 0)}
        </span>
        <span className="text-xs text-gray-500 group-hover:text-gray-300">
          By:{" "}
          <Link to={`/profile/${blog.author?.username || "unknown"}`} className="hover:underline">
            {blog.author?.username || "Unknown"}
          </Link>
          {blog.isPrivate && (
            <LockClosedIcon className="h-3 w-3 inline ml-1 text-red-500" />
          )}
        </span>
      </div>
    </div>
  </Link>
);

const Home = () => {
  const location = useLocation();
  const navigate = useNavigate();
  // Get section from URL query (defaults to discussions)
  useEffect(() => {
    const params = new URLSearchParams(location.search);
    setActiveSection(params.get("section") || "discussions");
  }, [location.search]);

  const [activeSection, setActiveSection] = useState("discussions");
  const [discussions, setDiscussions] = useState([]);
  const [debates, setDebates] = useState([]);
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(false);

  // Fetch all data
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const [disRes, debRes, blogRes] = await Promise.all([
          API.get("/discussions"),
          API.get("/debates"),
          API.get("/blogs"),
        ]);
        setDiscussions(Array.isArray(disRes.data) ? disRes.data : []);
        setDebates(Array.isArray(debRes.data) ? debRes.data : []);
        setBlogs(Array.isArray(blogRes.data) ? blogRes.data : []);
      } catch (err) {
        console.error("Error fetching data:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  // Sort items by popularity and slice top 9
  const getPopularItems = useCallback(
    (items) =>
      [...items]
        .sort((a, b) => {
          const popA = (a.comments?.length || 0) + (a.upvotes?.length || 0) + (a.downvotes?.length || 0);
          const popB = (b.comments?.length || 0) + (b.upvotes?.length || 0) + (b.downvotes?.length || 0);
          return popB - popA;
        })
        .slice(0, 9),
    []
  );

  // Render the appropriate cards based on active section
  const renderCards = () => {
    if (activeSection === "discussions")
      return getPopularItems(discussions).map((d) => <DiscussionCard key={d._id} discussion={d} />);
    if (activeSection === "debates")
      return getPopularItems(debates).map((d) => <DebateCard key={d._id} debate={d} />);
    if (activeSection === "blogs")
      return getPopularItems(blogs).map((b) => <BlogCard key={b._id} blog={b} />);
  };

  // See More button – rendered only if there are more than 9 items
  const renderSeeMore = () => {
    if (activeSection === "discussions" && discussions.length > 9)
      return <Link to="/discussions" className="flex items-center justify-center bg-blue-500 text-white rounded-full p-2 mt-4 transition hover:bg-blue-600">See More</Link>;
    if (activeSection === "debates" && debates.length > 9)
      return <Link to="/debates" className="flex items-center justify-center bg-blue-500 text-white rounded-full p-2 mt-4 transition hover:bg-blue-600">See More</Link>;
    if (activeSection === "blogs" && blogs.length > 9)
      return <Link to="/blogs" className="flex items-center justify-center bg-blue-500 text-white rounded-full p-2 mt-4 transition hover:bg-blue-600">See More</Link>;
    return null;
  };

  return (
    <div className="px-2 md:px-8 pt-6 md:pt-10">
      {/* Section Toggle */}
      <div className="flex flex-col md:flex-row gap-4 mb-6 md:ml-64">
        {["discussions", "debates", "blogs"].map((sec) => (
          <button
            key={sec}
            onClick={() => {
              setActiveSection(sec);
              navigate(`/?section=${sec}`);
            }}
            className={`px-4 py-2 rounded-full text-sm md:text-base transition ${
              activeSection === sec ? "bg-blue-500 text-white" : "bg-gray-200 text-gray-800"
            }`}
          >
            {sec.charAt(0).toUpperCase() + sec.slice(1)}
          </button>
        ))}
      </div>

      {loading ? (
        <p className="text-center text-gray-500">Loading...</p>
      ) : (
        <>
          {/* Cards Grid */}
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4 md:ml-64">
            {renderCards()}
          </div>
          {renderSeeMore()}
        </>
      )}
    </div>
  );
};

export default Home;
