import React, { useEffect, useState, useCallback } from "react";
import { useParams, useNavigate } from "react-router-dom";
import API from "../api/axiosInstance";
import { UserRemoveIcon, CogIcon, CalendarIcon, PencilIcon, CameraIcon, TrashIcon, MapPinIcon } from "@heroicons/react/outline";

const BACKEND_BASE_URL = "http://localhost:5001";

function getToken() {
  const token = localStorage.getItem("token");
  return token;
}

const ProfilePage = () => {
  const { username } = useParams();
  const navigate = useNavigate();

  const [profileUser, setProfileUser] = useState(null);
  const [currentUser, setCurrentUser] = useState(null);
  const [createdDebates, setCreatedDebates] = useState([]);
  const [createdBlogs, setCreatedBlogs] = useState([]);
  const [createdDiscussions, setCreatedDiscussions] = useState([]);
  const [profileError, setProfileError] = useState("");
  const [isFollowing, setIsFollowing] = useState(false);
  const [isFollower, setIsFollower] = useState(false);
  const [activeTab, setActiveTab] = useState("debate");
  const [showFollowers, setShowFollowers] = useState(false);
  const [showFollowing, setShowFollowing] = useState(false);
  const [showSettingsMenu, setShowSettingsMenu] = useState(false);
  const [showChangeBioModal, setShowChangeBioModal] = useState(false);
  const [showChangeProfilePictureModal, setShowChangeProfilePictureModal] = useState(false);
  const [showChangeAddressModal, setShowChangeAddressModal] = useState(false);
  const [newBio, setNewBio] = useState("");
  const [newAddress, setNewAddress] = useState("");
  const [newProfilePicture, setNewProfilePicture] = useState(null);
  const [loading, setLoading] = useState(true);

  const token = getToken();

  const fetchCurrentUser = useCallback(async () => {
    if (!token) return null;
    const res = await API.get("/users/profile/me", { headers: { Authorization: `Bearer ${token}` } });
    return res.data;
  }, [token]);

  const fetchProfile = useCallback(async () => {
    const res = await API.get(`/users/profile/${username}`, { headers: { Authorization: `Bearer ${token}` } });
    return res.data;
  }, [username, token]);

  const fetchCreatedDebates = useCallback(async () => {
    const res = await API.get(`/users/profile/${username}/debates`, { headers: { Authorization: `Bearer ${token}` } });
    return res.data.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
  }, [username, token]);

  const fetchCreatedBlogs = useCallback(async () => {
    const res = await API.get(`/users/profile/${username}/blogs`, { headers: { Authorization: `Bearer ${token}` } });
    return res.data.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
  }, [username, token]);

  const fetchCreatedDiscussions = useCallback(async () => {
    const res = await API.get(`/users/profile/${username}/discussions`, { headers: { Authorization: `Bearer ${token}` } });
    return res.data.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
  }, [username, token]);

  const fetchAllData = useCallback(async () => {
    setLoading(true);
    setProfileUser(null);
    setCreatedDebates([]);
    setCreatedBlogs([]);
    setCreatedDiscussions([]);
    setProfileError("");

    try {
      const [currentUserData, profileData, debatesData, blogsData, discussionsData] = await Promise.all([
        fetchCurrentUser(),
        fetchProfile(),
        fetchCreatedDebates(),
        fetchCreatedBlogs(),
        fetchCreatedDiscussions(),
      ]);

      setCurrentUser(currentUserData);
      setProfileUser(profileData);
      setCreatedDebates(debatesData);
      setCreatedBlogs(blogsData);
      setCreatedDiscussions(discussionsData);

      setNewBio(profileData.bio || "");
      setNewAddress(profileData.address || "");
      const followers = Array.isArray(profileData.followers) ? profileData.followers : [];
      const followings = Array.isArray(profileData.followings) ? profileData.followings : [];
      if (currentUserData) {
        setIsFollowing(followers.some((f) => String(f._id) === String(currentUserData._id)));
        setIsFollower(followings.some((f) => String(f._id) === String(currentUserData._id)));
      }
    } catch (err) {
      console.error("Error fetching data:", err);
      setProfileError(err.response?.data?.message || "Error fetching profile data");
    } finally {
      setLoading(false);
    }
  }, [fetchCurrentUser, fetchProfile, fetchCreatedDebates, fetchCreatedBlogs, fetchCreatedDiscussions]);

  useEffect(() => {
    fetchAllData();
  }, [username, fetchAllData]);

  const handleChangeBio = async () => {
    try {
      if (!token) throw new Error("No authentication token found");
      await API.put("/users/profile/update-bio", { bio: newBio }, { headers: { Authorization: `Bearer ${token}` } });
      setShowChangeBioModal(false);
      fetchAllData();
    } catch (err) {
      setProfileError(err.response?.data?.message || "Error updating bio");
    }
  };

  const handleChangeAddress = async () => {
    try {
      if (!token) throw new Error("No authentication token found");
      await API.put("/users/profile/update-address", { address: newAddress }, { headers: { Authorization: `Bearer ${token}` } });
      setShowChangeAddressModal(false);
      fetchAllData();
    } catch (err) {
      setProfileError(err.response?.data?.message || "Error updating address");
    }
  };

  const handleChangeProfilePicture = async () => {
    if (!newProfilePicture) {
      setProfileError("Please select a file to upload.");
      return;
    }
    const formData = new FormData();
    formData.append("profilePicture", newProfilePicture);
    try {
      await API.put("/users/profile/update-profile-picture", formData, { headers: { Authorization: `Bearer ${token}` } });
      setShowChangeProfilePictureModal(false);
      setNewProfilePicture(null);
      fetchAllData();
    } catch (err) {
      setProfileError(err.response?.data?.message || "Error updating profile picture");
    }
  };

  const handleDeleteDebate = async (debateId) => {
    try {
      await API.delete(`/debates/${debateId}`, { headers: { Authorization: `Bearer ${token}` } });
      fetchAllData();
    } catch (err) {
      setProfileError(err.response?.data?.message || "Error deleting debate");
    }
  };

  const handleDeleteDiscussion = async (discussionId) => {
    try {
      await API.delete(`/discussions/${discussionId}`, { headers: { Authorization: `Bearer ${token}` } });
      fetchAllData();
    } catch (err) {
      setProfileError(err.response?.data?.message || "Error deleting discussion");
    }
  };

  const handleDeleteBlog = async (blogId) => {
    try {
      await API.delete(`/blogs/${blogId}`, { headers: { Authorization: `Bearer ${token}` } });
      fetchAllData();
    } catch (err) {
      setProfileError(err.response?.data?.message || "Error deleting blog");
    }
  };

  const handleLogOut = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  const handleFollow = async () => {
    if (!token) {
      alert("You must be logged in to follow or unfollow!");
      return navigate("/login");
    }
    try {
      if (isFollowing) {
        await API.post(`/users/profile/${username}/unfollow`, {}, { headers: { Authorization: `Bearer ${token}` } });
      } else {
        await API.post(`/users/profile/${username}/follow`, {}, { headers: { Authorization: `Bearer ${token}` } });
      }
      fetchAllData();
    } catch (err) {
      setProfileError(err.response?.data?.message || "Error following/unfollowing");
    }
  };

  const handleChat = () => {
    if (!token) {
      alert("You must be logged in to chat!");
      return navigate("/login");
    }
    navigate(`/chat/${username}`);
  };

  const handleDebateClick = (debateId) => navigate(`/debatepage/${debateId}`);
  const handleDiscussionClick = (discussionId) => navigate(`/discussionpage/${discussionId}`);
  const handleBlogClick = (blogId) => navigate(`/blogpage/${blogId}`);
  const handleProfileClick = (username) => {
    setShowFollowers(false);
    setShowFollowing(false);
    navigate(`/profile/${username}`);
  };

  const isOwnProfile = currentUser && profileUser && currentUser._id === profileUser._id;
  const totalPosts = (createdDebates.length || 0) + (createdDiscussions.length || 0) + (createdBlogs.length || 0);
  const followButtonText = isFollowing ? "Unfollow" : isFollower ? "Follow Back" : "Follow";

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-b from-gray-900 to-gray-800 flex items-center justify-center">
        <span className="text-xl text-white animate-pulse font-semibold">Loading your profile...</span>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-900 to-gray-800 text-white">
      {/* Error Message */}
      {profileError && (
        <div className="max-w-4xl mx-auto px-6 mt-6">
          <div className="bg-red-600 text-white p-4 rounded-xl shadow-lg">{profileError}</div>
        </div>
      )}

      {/* Header Section */}
      <header className="py-12 bg-gray-900 bg-opacity-90 shadow-lg">
        <div className="max-w-4xl mx-auto px-6">
          <div className="flex flex-col items-center md:flex-row md:items-start">
            <div className="relative group mb-8 md:mb-0 md:mr-10">
              <img
                src={
                  profileUser?.profilePicture
                    ? `${BACKEND_BASE_URL}${profileUser.profilePicture}`
                    : `${BACKEND_BASE_URL}/images/default-avatar.png`
                }
                alt="profile"
                className="w-36 h-36 rounded-full object-cover border-4 border-indigo-500 shadow-xl group-hover:scale-110 transition-transform duration-300"
                onError={(e) => (e.target.src = `${BACKEND_BASE_URL}/images/default-avatar.png`)}
              />
              {isOwnProfile && (
                <button
                  onClick={() => setShowChangeProfilePictureModal(true)}
                  className="absolute bottom-1 right-1 p-2 bg-indigo-600 text-white rounded-full hover:bg-indigo-700 transition-colors duration-300 shadow-md"
                >
                  <CameraIcon className="h-5 w-5" />
                </button>
              )}
            </div>
            <div className="text-center md:text-left">
              <div className="flex items-center justify-center md:justify-start mb-4">
                <h1 className="text-4xl font-extrabold text-white tracking-tight">
                  {profileUser?.username || "Loading..."}
                </h1>
                {isOwnProfile ? (
                  <button
                    onClick={() => setShowSettingsMenu(!showSettingsMenu)}
                    className="ml-4 p-2 bg-gray-700 rounded-full hover:bg-gray-600 transition-colors duration-300"
                  >
                    <CogIcon className="h-6 w-6 text-white" />
                  </button>
                ) : (
                  <div className="ml-4 flex space-x-3">
                    <button
                      onClick={handleFollow}
                      className={`px-5 py-2 rounded-full text-white font-semibold ${
                        isFollowing ? "bg-gray-600 hover:bg-gray-700" : "bg-indigo-600 hover:bg-indigo-700"
                      } transition-all duration-300 shadow-md`}
                    >
                      {followButtonText}
                    </button>
                    <button
                      onClick={handleChat}
                      className="px-5 py-2 rounded-full bg-green-600 text-white font-semibold hover:bg-green-700 transition-all duration-300 shadow-md"
                    >
                      Message
                    </button>
                  </div>
                )}
              </div>
              {showSettingsMenu && isOwnProfile && (
                <div className="absolute mt-2 w-56 bg-gray-800 rounded-xl shadow-2xl z-10 border border-gray-700 animate-fade-in">
                  <ul className="py-2 text-sm">
                    <li
                      className="px-4 py-3 hover:bg-gray-700 cursor-pointer flex items-center transition-colors duration-200"
                      onClick={() => {
                        setShowChangeBioModal(true);
                        setShowSettingsMenu(false);
                      }}
                    >
                      <PencilIcon className="h-5 w-5 mr-2" />
                      Change Bio
                    </li>
                    <li
                      className="px-4 py-3 hover:bg-gray-700 cursor-pointer flex items-center transition-colors duration-200"
                      onClick={() => {
                        setShowChangeAddressModal(true);
                        setShowSettingsMenu(false);
                      }}
                    >
                      <MapPinIcon className="h-5 w-5 mr-2" />
                      Change Address
                    </li>
                    <li
                      className="px-4 py-3 hover:bg-gray-700 cursor-pointer flex items-center transition-colors duration-200"
                      onClick={handleLogOut}
                    >
                      <UserRemoveIcon className="h-5 w-5 mr-2" />
                      Log Out
                    </li>
                  </ul>
                </div>
              )}
              <div className="flex justify-center md:justify-start space-x-12 mb-6">
                <div className="flex items-center group cursor-pointer hover:text-indigo-400 transition-colors duration-300">
                  <span className="font-bold text-xl group-hover:scale-105 transition-transform duration-200">
                    {totalPosts}
                  </span>
                  <span className="text-gray-300 ml-2">Posts</span>
                </div>
                <div
                  onClick={() => setShowFollowers(true)}
                  className="flex items-center group cursor-pointer hover:text-indigo-400 transition-colors duration-300"
                >
                  <span className="font-bold text-xl group-hover:scale-105 transition-transform duration-200">
                    {profileUser?.followers?.length || 0}
                  </span>
                  <span className="text-gray-300 ml-2">Followers</span>
                </div>
                <div
                  onClick={() => setShowFollowing(true)}
                  className="flex items-center group cursor-pointer hover:text-indigo-400 transition-colors duration-300"
                >
                  <span className="font-bold text-xl group-hover:scale-105 transition-transform duration-200">
                    {profileUser?.followings?.length || 0}
                  </span>
                  <span className="text-gray-300 ml-2">Following</span>
                </div>
              </div>
              <p className="text-gray-300 italic text-lg">{profileUser?.bio || "No bio yet."}</p>
              <div className="flex items-center justify-center md:justify-start text-gray-400 text-sm mt-3">
                <CalendarIcon className="h-5 w-5 mr-2" />
                Joined: {profileUser?.createdAt ? new Date(profileUser.createdAt).toLocaleDateString("en-US", {
                  year: "numeric",
                  month: "long",
                  day: "numeric",
                }) : "Unknown"}
              </div>
              {profileUser?.address && (
                <div className="flex items-center justify-center md:justify-start text-gray-400 text-sm mt-2">
                  <MapPinIcon className="h-5 w-5 mr-2" />
                  {profileUser.address}
                </div>
              )}
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <div className="max-w-4xl mx-auto px-6 py-10">
        {/* Tabs */}
        <div className="flex justify-center space-x-10 border-b border-gray-700 pb-4">
          {["debate", "discussion", "blogs"].map((tab) => (
            <button
              key={tab}
              onClick={() => setActiveTab(tab)}
              className={`relative px-6 py-3 text-md font-semibold uppercase tracking-wide ${
                activeTab === tab ? "text-indigo-400" : "text-gray-400 hover:text-indigo-300"
              } transition-colors duration-300`}
            >
              {tab}
              {activeTab === tab && (
                <span className="absolute bottom-0 left-0 w-full h-1 bg-indigo-500 rounded-full transform scale-x-100 transition-transform duration-300"></span>
              )}
            </button>
          ))}
        </div>

        {/* Posts Grid */}
        <div className="mt-8 grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
          {activeTab === "debate" && (
            createdDebates.length > 0 ? (
              createdDebates.map((deb) => (
                <div
                  key={deb._id}
                  className="bg-gray-800 rounded-xl p-5 cursor-pointer hover:shadow-2xl hover:scale-105 transition-all duration-300 shadow-lg relative group"
                >
                  <h3 className="text-md font-semibold text-white truncate">{deb.title || "Untitled Debate"}</h3>
                  <p className="text-gray-300 text-sm mt-2 line-clamp-2">
                    {deb.openingArgument || "No opening argument"}
                  </p>
                  {isOwnProfile && (
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDeleteDebate(deb._id);
                      }}
                      className="absolute top-3 right-3 p-2 bg-red-600 text-white rounded-full hover:bg-red-700 transition-colors duration-300 opacity-0 group-hover:opacity-100"
                    >
                      <TrashIcon className="h-4 w-4" />
                    </button>
                  )}
                  <div onClick={() => handleDebateClick(deb._id)} className="absolute inset-0"></div>
                </div>
              ))
            ) : (
              <p className="col-span-3 text-center text-gray-400 text-lg">No debates created yet.</p>
            )
          )}
          {activeTab === "discussion" && (
            createdDiscussions.length > 0 ? (
              createdDiscussions.map((disc) => (
                <div
                  key={disc._id}
                  className="bg-gray-800 rounded-xl p-5 cursor-pointer hover:shadow-2xl hover:scale-105 transition-all duration-300 shadow-lg relative group"
                >
                  <h3 className="text-md font-semibold text-white truncate">{disc.title || "Untitled Discussion"}</h3>
                  <p className="text-gray-300 text-sm mt-2 line-clamp-2">{disc.description || "No description"}</p>
                  {isOwnProfile && (
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDeleteDiscussion(disc._id);
                      }}
                      className="absolute top-3 right-3 p-2 bg-red-600 text-white rounded-full hover:bg-red-700 transition-colors duration-300 opacity-0 group-hover:opacity-100"
                    >
                      <TrashIcon className="h-4 w-4" />
                    </button>
                  )}
                  <div onClick={() => handleDiscussionClick(disc._id)} className="absolute inset-0"></div>
                </div>
              ))
            ) : (
              <p className="col-span-3 text-center text-gray-400 text-lg">No discussions created yet.</p>
            )
          )}
          {activeTab === "blogs" && (
            createdBlogs.length > 0 ? (
              createdBlogs.map((blog) => (
                <div
                  key={blog._id}
                  className="bg-gray-800 rounded-xl p-5 cursor-pointer hover:shadow-2xl hover:scale-105 transition-all duration-300 shadow-lg relative group"
                >
                  <h3 className="text-md font-semibold text-white truncate">{blog.title || "Untitled Blog"}</h3>
                  <p className="text-gray-300 text-sm mt-2 line-clamp-2">{blog.content || "No content"}</p>
                  {blog.fileUrl && blog.fileUrl.match(/\.(jpeg|jpg|png)$/i) && (
                    <img
                      src={`${BACKEND_BASE_URL}${blog.fileUrl}`}
                      alt="Blog media"
                      className="w-full h-40 object-cover rounded-lg mt-3 hover:opacity-90 transition-opacity duration-200"
                      onError={(e) => (e.target.src = `${BACKEND_BASE_URL}/images/default-image.png`)}
                    />
                  )}
                  {isOwnProfile && (
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDeleteBlog(blog._id);
                      }}
                      className="absolute top-3 right-3 p-2 bg-red-600 text-white rounded-full hover:bg-red-700 transition-colors duration-300 opacity-0 group-hover:opacity-100"
                    >
                      <TrashIcon className="h-4 w-4" />
                    </button>
                  )}
                  <div onClick={() => handleBlogClick(blog._id)} className="absolute inset-0"></div>
                </div>
              ))
            ) : (
              <p className="col-span-3 text-center text-gray-400 text-lg">No blogs yet.</p>
            )
          )}
        </div>
      </div>

      {/* Followers Modal */}
      {showFollowers && (
        <div className="fixed inset-0 bg-black bg-opacity-60 flex justify-center items-center">
          <div className="bg-gray-800 p-8 rounded-xl shadow-2xl max-w-lg w-full max-h-[80vh] overflow-y-auto animate-fade-in">
            <h2 className="text-2xl font-bold text-white mb-6 text-center">Followers</h2>
            {profileUser?.followers?.length > 0 ? (
              <ul className="space-y-4">
                {profileUser.followers.map((follower) => (
                  <li
                    key={follower._id}
                    className="flex items-center space-x-4 p-3 bg-gray-700 rounded-lg cursor-pointer hover:bg-gray-600 transition-colors duration-200"
                    onClick={() => handleProfileClick(follower.username)}
                  >
                    <img
                      src={
                        follower.profilePicture
                          ? `${BACKEND_BASE_URL}${follower.profilePicture}`
                          : `${BACKEND_BASE_URL}/images/default-avatar.png`
                      }
                      alt={follower.username}
                      className="w-12 h-12 rounded-full border-2 border-indigo-500"
                    />
                    <span className="text-md font-medium text-white">{follower.username}</span>
                  </li>
                ))}
              </ul>
            ) : (
              <p className="text-center text-gray-400 text-lg">No followers yet.</p>
            )}
            <button
              onClick={() => setShowFollowers(false)}
              className="mt-6 w-full bg-gray-700 text-white px-5 py-2 rounded-lg hover:bg-gray-600 transition-colors duration-300"
            >
              Close
            </button>
          </div>
        </div>
      )}

      {/* Following Modal */}
      {showFollowing && (
        <div className="fixed inset-0 bg-black bg-opacity-60 flex justify-center items-center">
          <div className="bg-gray-800 p-8 rounded-xl shadow-2xl max-w-lg w-full max-h-[80vh] overflow-y-auto animate-fade-in">
            <h2 className="text-2xl font-bold text-white mb-6 text-center">Following</h2>
            {profileUser?.followings?.length > 0 ? (
              <ul className="space-y-4">
                {profileUser.followings.map((following) => (
                  <li
                    key={following._id}
                    className="flex items-center space-x-4 p-3 bg-gray-700 rounded-lg cursor-pointer hover:bg-gray-600 transition-colors duration-200"
                    onClick={() => handleProfileClick(following.username)}
                  >
                    <img
                      src={
                        following.profilePicture
                          ? `${BACKEND_BASE_URL}${following.profilePicture}`
                          : `${BACKEND_BASE_URL}/images/default-avatar.png`
                      }
                      alt={following.username}
                      className="w-12 h-12 rounded-full border-2 border-indigo-500"
                    />
                    <span className="text-md font-medium text-white">{following.username}</span>
                  </li>
                ))}
              </ul>
            ) : (
              <p className="text-center text-gray-400 text-lg">Not following anyone yet.</p>
            )}
            <button
              onClick={() => setShowFollowing(false)}
              className="mt-6 w-full bg-gray-700 text-white px-5 py-2 rounded-lg hover:bg-gray-600 transition-colors duration-300"
            >
              Close
            </button>
          </div>
        </div>
      )}

      {/* Change Bio Modal */}
      {showChangeBioModal && (
        <div className="fixed inset-0 bg-black bg-opacity-60 flex justify-center items-center">
          <div className="bg-gray-800 p-8 rounded-xl shadow-2xl max-w-lg w-full animate-fade-in">
            <h2 className="text-2xl font-bold text-white mb-6 text-center">Change Bio</h2>
            <textarea
              value={newBio}
              onChange={(e) => setNewBio(e.target.value)}
              className="w-full p-4 bg-gray-700 text-white rounded-lg border border-gray-600 focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-all duration-300"
              rows="4"
              placeholder="Enter your new bio..."
            />
            <div className="flex space-x-4 mt-6">
              <button
                onClick={handleChangeBio}
                className="flex-1 bg-indigo-600 text-white px-5 py-2 rounded-lg hover:bg-indigo-700 transition-colors duration-300 shadow-md"
              >
                Save
              </button>
              <button
                onClick={() => setShowChangeBioModal(false)}
                className="flex-1 bg-gray-700 text-white px-5 py-2 rounded-lg hover:bg-gray-600 transition-colors duration-300"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Change Address Modal */}
      {showChangeAddressModal && (
        <div className="fixed inset-0 bg-black bg-opacity-60 flex justify-center items-center">
          <div className="bg-gray-800 p-8 rounded-xl shadow-2xl max-w-lg w-full animate-fade-in">
            <h2 className="text-2xl font-bold text-white mb-6 text-center">Change Address</h2>
            <input
              type="text"
              value={newAddress}
              onChange={(e) => setNewAddress(e.target.value)}
              className="w-full p-4 bg-gray-700 text-white rounded-lg border border-gray-600 focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-all duration-300"
              placeholder="Enter your new address..."
            />
            <div className="flex space-x-4 mt-6">
              <button
                onClick={handleChangeAddress}
                className="flex-1 bg-indigo-600 text-white px-5 py-2 rounded-lg hover:bg-indigo-700 transition-colors duration-300 shadow-md"
              >
                Save
              </button>
              <button
                onClick={() => setShowChangeAddressModal(false)}
                className="flex-1 bg-gray-700 text-white px-5 py-2 rounded-lg hover:bg-gray-600 transition-colors duration-300"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Change Profile Picture Modal */}
      {showChangeProfilePictureModal && (
        <div className="fixed inset-0 bg-black bg-opacity-60 flex justify-center items-center">
          <div className="bg-gray-800 p-8 rounded-xl shadow-2xl max-w-lg w-full animate-fade-in">
            <h2 className="text-2xl font-bold text-white mb-6 text-center">Change Profile Picture</h2>
            <input
              type="file"
              accept="image/*"
              onChange={(e) => setNewProfilePicture(e.target.files[0])}
              className="w-full p-3 bg-gray-700 text-white rounded-lg border border-gray-600 focus:outline-none focus:ring-2 focus:ring-indigo-500"
            />
            <div className="flex space-x-4 mt-6">
              <button
                onClick={handleChangeProfilePicture}
                className="flex-1 bg-indigo-600 text-white px-5 py-2 rounded-lg hover:bg-indigo-700 transition-colors duration-300 shadow-md"
              >
                Upload
              </button>
              <button
                onClick={() => setShowChangeProfilePictureModal(false)}
                className="flex-1 bg-gray-700 text-white px-5 py-2 rounded-lg hover:bg-gray-600 transition-colors duration-300"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProfilePage;
